export const findLatestDate = (commits) => {
  if (!commits.length) return;

  const latestCommit = commits[0];

  return latestCommit.commit.author.date;
};

export const findCommitsDate = async (branch, sinceDate = "") => {
  const result = await fetch(
    `https://api.github.com/repos/eduardocastaneda/timereporting/commits?per_page=100&sha=${branch}&since=${sinceDate}`,
    {
      headers: {
        Authorization: `token ${process.env.GITHUB_TOKEN}`
      }
    }
  );

  return result.json();
};
