import fetch from "isomorphic-unfetch";
import { findLatestDate } from "../lib/helpers/commits";
import { addSecond } from "../lib/helpers/date";

function HomePage({ commits }) {
  const commitsElements = commits.map((commit) => {
    let message = commit.commit.message;
    if (message.indexOf("*") > -1) {
      message = message.split("*")[0];
    }

    return <li key={commit.sha}>{message}</li>;
  });

  return (
    <div>
      Next merge/deploy to Master
      <ul>{commitsElements}</ul>
    </div>
  );
}

HomePage.getInitialProps = async () => {
  console.log(process.env.MASTER_BRANCH);
  const resMaster = await fetch(
    `https://api.github.com/repos/eduardocastaneda/timereporting/commits?per_page=100&sha=${process.env.MASTER_BRANCH}`,
    {
      headers: {
        Authorization: `token ${process.env.GITHUB_TOKEN}`
      }
    }
  );
  const resMasterJson = await resMaster.json();
  const dateString = findLatestDate(resMasterJson);
  const sinceDate = addSecond(dateString);

  const resDeveloper = await fetch(
    `https://api.github.com/repos/eduardocastaneda/timereporting/commits?per_page=100&sha=${process.env.DEVELOP_BRANCH}&since=${sinceDate}`,
    {
      headers: {
        Authorization: `token ${process.env.GITHUB_TOKEN}`
      }
    }
  );

  const commits = await resDeveloper.json();
  return { commits };
};

export default HomePage;
