import fetch from "isomorphic-unfetch";
import { findLatestDate, fetchCommits } from "../lib/helpers/commits";
import { addSecond } from "../lib/helpers/date";

function HomePage({ commits }) {
  const commitsElements = commits.map((data) => {
    let { message, author } = data.commit;

    if (message.indexOf("*") > -1) {
      message = message.split("*")[0];
    }

    return <li key={author.date}>{message}</li>;
  });

  return (
    <div>
      Next merge/deploy to Master
      <ul>{commitsElements}</ul>
    </div>
  );
}

HomePage.getInitialProps = async () => {
  const masterCommits = await fetchCommits(process.env.MASTER_BRANCH);
  const latestCommitDate = findLatestDate(masterCommits);

  const sinceDate = addSecond(latestCommitDate);
  const commits = await fetchCommits(process.env.DEVELOP_BRANCH, sinceDate);

  return { commits };
};

export default HomePage;
